import React, { useState, useEffect } from "react";
import "./Tooth.css";

const Tooth = ({ tooth }) => {
    const [toothName, setName] = useState("");
    const [issues, setIssues] = useState([]);
    const [notes, setNotes] = useState("");

    useEffect(() => {
        if (tooth) {
            setName(tooth.name);
            setIssues(tooth.issues || []);
            setNotes(tooth.notes || "Nicio notă suplimentară");
        }
    }, [tooth]);

    return (
        <div className="tooth-details">
            <h2>Detalii despre dinte</h2>
            <div className="tooth-info">
                <p><strong>Nume dinte:</strong> {toothName}</p>
                <p><strong>Probleme:</strong></p>
                {issues.length > 0 ? (
                    <ul className="issues-list">
                        {issues.map((issue, index) => (
                            <li key={index} className="issue-item">
                                {issue}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="no-issues">Nicio problemă identificată</p>
                )}

            </div>
        </div>
    );
};

export default Tooth;
