import React, { useEffect, useState } from 'react';
import {useParams, useLocation, Route, Routes} from 'react-router-dom';
import "./Patients.css";
import Tooth from "./Tooth";

const Patient = () => {
    const { id } = useParams();
    const location = useLocation();
    const patient = location.state?.patient;

    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');

    useEffect(() => {
        if (patient) {
            setFirstName(patient.firstName);
            setLastName(patient.lastName);
            setPhoneNumber(patient.phoneNumber);
        } else {
            // Fetch patient data from API if not passed through state
            fetch(`/api/patients/${id}`)
                .then(res => res.json())
                .then(data => {
                    setFirstName(data.firstName);
                    setLastName(data.lastName);
                    setPhoneNumber(data.phoneNumber);
                })
                .catch(error => console.error("Error fetching patient:", error));
        }
    }, [patient, id]);

    const handleSubmit= () => {
        console.log(`Teeth clicked`);
        navigate(`/tooth`);
    };


    return (
        <div className='main-content'>
            <div className='left-content'>
                <form onSubmit={handleSubmit}>
                    <div className="patient-form">
                        <input
                            className="input-bar"
                            type="text"
                            value={firstName}
                            onChange={(e) => setFirstName(e.target.value)}
                            placeholder="Prenume"
                            required
                        />
                        <input
                            className="input-bar"
                            type="text"
                            value={lastName}
                            onChange={(e) => setLastName(e.target.value)}
                            placeholder="Nume de familie"
                            required
                        />
                        <input
                            className="input-bar"
                            type="text"
                            value={phoneNumber}
                            onChange={(e) => setPhoneNumber(e.target.value)}
                            placeholder="Număr de telefon"
                            required
                        />
                    </div>

                    <button type="submit" className="submit-button" >Salvează modificările</button>
                </form>


            </div>

            <div className="right-content">
                <Routes>
                    <Route path="/teeth" element={<Tooth />} />
                </Routes>
            </div>


        </div>
    );
};

export default Patient;
