import React, { useEffect, useState } from 'react';
import {useParams, useLocation, Route, Routes, useNavigate} from 'react-router-dom';
import "./Patients.css";
import Tooth from "./Tooth";

const Patient = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const location = useLocation();
    const patient = location.state?.patient;

    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');
    const [selectedTooth, setSelectedTooth] = useState(null); // Dintele selectat
    const [teeth, setTeeth] = useState([]);

    useEffect(() => {
        if (patient) {
            setFirstName(patient.firstName);
            setLastName(patient.lastName);
            setPhoneNumber(patient.phoneNumber);
            setTeeth(patient.teeths);
        } else {
            // Fetch patient data from API if not passed through state
            fetch(`/api/patients/${id}`)
                .then(res => res.json())
                .then(data => {
                    setFirstName(data.firstName);
                    setLastName(data.lastName);
                    setPhoneNumber(data.phoneNumber);
                })
                .catch(error => console.error("Error fetching patient:", error));
        }
    }, [patient, id]);

    const handleSubmit= () => {
        console.log(`Teeth clicked`);
        navigate(`tooth`);
    };
    // Funcția pentru a seta dintele selectat
    const handleToothClick = (tooth) => {
        console.log(`Teeth clicked`);
        console.log(tooth);
        setSelectedTooth(tooth);
    };

    return (
        <div className='main-content'>
            <div className='left-content'>
                <form onSubmit={handleSubmit}>
                    <div className="patient-form">
                        <input
                            className="input-bar"
                            type="text"
                            value={firstName}
                            onChange={(e) => setFirstName(e.target.value)}
                            placeholder="Prenume"
                            required
                        />
                        <input
                            className="input-bar"
                            type="text"
                            value={lastName}
                            onChange={(e) => setLastName(e.target.value)}
                            placeholder="Nume de familie"
                            required
                        />
                        <input
                            className="input-bar"
                            type="text"
                            value={phoneNumber}
                            onChange={(e) => setPhoneNumber(e.target.value)}
                            placeholder="Număr de telefon"
                            required
                        />
                    </div>

                    <button type="submit" className="submit-button" >Salvează modificările</button>
                </form>
                <div className="patient-form">
                    <h3>Dinți:</h3>
                    <ul>
                        {teeth.map((tooth) => (
                            <li
                                key={tooth.id}
                                onClick={() => handleToothClick(tooth)} // Setăm dintele selectat
                                // style={{
                                //     cursor: "pointer",
                                //     color: selectedTooth?.id === tooth.id ? "blue" : "black",
                                // }}
                            >
                                Dintele {tooth.name}
                            </li>
                        ))}
                    </ul>
                </div>



            </div>




        </div>
    );
};

export default Patient;
